import utilStyles from '../../../styles/utils.module.css'
import { Row, Col, Form, Button, Modal, Container } from 'react-bootstrap'
import 'antd/dist/antd.css';
import { Upload, Select, message } from 'antd';
import { CompassOutlined, UploadOutlined } from '@ant-design/icons';
import React, { useEffect } from 'react'


export default function EditMenuModal(props) {

    let { menu } = props
    let { onHide, edit_menu } = props
    const defaultImage = "data:image/png;base64,"
    const [menuImage, setMenuImage] = React.useState(defaultImage)
    const [menuName, setMenuName] = React.useState('')
    const [errors, setErrors] = React.useState({});
    const [menuForm, setMenuForm] = React.useState({
        menuName: '',
        description: '',
        price: ''
    })
    useEffect(() => {
        if (menu !== undefined) {
            setMenuForm({
                menuName: menu.name,
                description: menu.description,
                price: menu.price,
                categoryId: menu.categoryId,
                menuId: menu.id
            })
            setMenuImage(menu.image_url)
            setMenuName(menu.name)
        }
    }, [props])


    const beforeUpload = (file) => {
        const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png';
        if (!isJpgOrPng) {
            message.error('You can only upload JPG/PNG file!');
        }
        const isLt2M = file.size / 1024 / 1024 < 2;
        if (!isLt2M) {
            message.error('Image must smaller than 2MB!');
        }
        return isJpgOrPng && isLt2M;
    }

    const handleUpload = (info) => {
        if (info.file.status === 'uploading') {
            return;
        }
        if (info.file.status === 'done') {
            getBase64(info.file.originFileObj, imageUrl => {
                setMenuform('menuImage', info.file.originFileObj)
                setMenuImage(imageUrl)
            })
        }
    };

    const getBase64 = (img, callback) => {
        const reader = new FileReader();
        reader.addEventListener('load', () => callback(reader.result));
        reader.readAsDataURL(img);
    }

    const setMenuform = (fieldName, value) => {
        // console.log
        setMenuForm({
            ...menuForm,
            [fieldName]: value
        })

        // Check and see if errors exist, and remove them from the error object:
        if (!!errors[fieldName]) setErrors({
            ...errors,
            [fieldName]: null
        })
    }

    const saveMenu = () => {
        const newErrors = findAddMenuFormErrors()

        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors)
        } else {
            edit_menu(menuForm)
            onHide()
        }
    }


    const findAddMenuFormErrors = () => {
        const { menuName, description, price } = menuForm
        const newErrors = {}
        // menu name  errors
        if (!menuName || menuName === '') newErrors.menuName = 'Menu name is required !'
        // description errors
        if (!description || description === '') newErrors.description = 'Description is required !'
        var pattern = new RegExp(/^\d+$/);
        // price errors
        if (!price || price === '') newErrors.price = 'Price is required !'
        else if (!pattern.test(price)) newErrors.price = 'Please enter valid price !'
        return newErrors
    }

    return (

        <Modal
            {...props}
            dialogClassName="menuModal-70w"
            aria-labelledby="contained-modal-title-vcenter"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title style={{ fontSize: "1.3rem" }}>
                    Menu : {menuName}
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Container>
                    <Row>
                        <Col sm={6}>
                            <Row>
                                <Col>
                                    <img src={menuImage} alt="avatar" style={{ width: '100%', height: '16rem', border: "1px solid #555", borderRadius: "5px" }} />
                                </Col>
                            </Row>
                            <br />
                            <Row>
                                <Col>
                                    <Upload
                                        showUploadList={false}
                                        beforeUpload={beforeUpload}
                                        onChange={(e) => handleUpload(e)}
                                        style={{ width: "100%" }}
                                    >
                                        <Button icon={<UploadOutlined />} className={utilStyles.cardText} style={{ width: "100%", backgroundColor: "#cfcfcf", color: "black", border: "none" }}>Click to Upload Menu Image</Button>
                                    </Upload>
                                </Col>
                            </Row>
                        </Col>
                        <Col sm={6}>
                            <Form onSubmit={saveMenu}>
                                <Form.Group >
                                    <Form.Label>Menu Name</Form.Label>
                                    <Form.Control
                                        type="text"
                                        onChange={(e) => setMenuform('menuName', e.target.value)}
                                        isInvalid={!!errors.menuName}
                                        value={menuForm.menuName}
                                    />
                                    <Form.Control.Feedback type="invalid">
                                        {errors.menuName}
                                    </Form.Control.Feedback>
                                </Form.Group>
                                <Form.Group >
                                    <Form.Label>Description</Form.Label>
                                    <Form.Control
                                        type="text"
                                        onChange={(e) => setMenuform('description', e.target.value)}
                                        isInvalid={!!errors.description}
                                        value={menuForm.description}
                                    />
                                    <Form.Control.Feedback type="invalid">
                                        {errors.description}
                                    </Form.Control.Feedback>
                                </Form.Group>
                                <Form.Group >
                                    <Form.Label>Price</Form.Label>
                                    <Form.Control
                                        type="text"
                                        onChange={(e) => setMenuform('price', e.target.value)}
                                        isInvalid={!!errors.price}
                                        value={menuForm.price}
                                    />
                                    <Form.Control.Feedback type="invalid">
                                        {errors.price}
                                    </Form.Control.Feedback>
                                </Form.Group>
                            </Form>
                        </Col>
                    </Row>
                </Container>
            </Modal.Body>
            <Modal.Footer>
                <Button onClick={() => saveMenu()}>
                    Save
                </Button>
                <Button onClick={props.onHide}>Close</Button>
            </Modal.Footer>

        </Modal >
    )

}