import utilStyles from '../../../styles/utils.module.css'
import { Row, Col, Form, Button } from 'react-bootstrap'
import 'antd/dist/antd.css';
import { Upload, message } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import partnerSerivce from '../../../services/partner'
import checkLogin from '../../../services/checkLogin'
import React, { useEffect } from 'react'
import WebComponent from './Web'
import MobileComponent from './Mobile'
import useMediaQuery from "../../../utils/utils";

export default function Promote(props) {

    let { restaurant_id } = props
    const isMobileResolution = useMediaQuery(768)
    const defaultImage = "data:image/png;base64,"
    const [promoteImageUrl, setPromoteImageUrl] = React.useState(defaultImage);
    const [promoteForm, setPromoteForm] = React.useState({
        promotedContents: '',
        bannerText: '',
        promotedImage: ''
    })
    const [errors, setErrors] = React.useState({});
    const [restaurantId, setRestaurantId] = React.useState();
    useEffect(() => {
        if (restaurant_id !== undefined) {
            setRestaurantId(restaurant_id)
        }
    }, [props])

    const findAddPromotionFormErrors = () => {
        const { promotedContents, bannerText } = promoteForm
        const newErrors = {}
        // Promoted contents errors
        if (!promotedContents || promotedContents === '') newErrors.promotedContents = 'Promoted contents is required !'
        // Banner text errors
        if (!bannerText || bannerText === '') newErrors.bannerText = 'Banner text is required !'
        return newErrors
    }

    const addPromotion = async (event) => {
        event.preventDefault();
        const newErrors = findAddPromotionFormErrors()
        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors)
        } else {
            const { promotedContents, bannerText, promotedImage } = promoteForm
            let data = {
                // restaurant === restaurantId
                restaurant: restaurantId,
                promote_content: promotedContents,
                title: bannerText,
                image: promotedImage
            }
            try {
                let response = await partnerSerivce.addPromotion(data)
                console.log(response)
                message.success('Promote promotion successful.')
                setPromoteImageUrl(defaultImage)
                setPromoteForm({
                    promotedContents: '',
                    bannerText: '',
                    promotedImage: ''
                })
            } catch (error) {
                console.log(error)
            }

        }
    }

    const setform = (fieldName, value) => {
        // console.log
        setPromoteForm({
            ...promoteForm,
            [fieldName]: value
        })

        // Check and see if errors exist, and remove them from the error object:
        if (!!errors[fieldName]) setErrors({
            ...errors,
            [fieldName]: null
        })
    }

    const handleUpload = (info) => {
        if (info.file.status === 'uploading') {
            return;
        }
        if (info.file.status === 'done') {
            getBase64(info.file.originFileObj, imageUrl => {
                setform('promotedImage', info.file.originFileObj)
                setPromoteImageUrl(imageUrl)
            })
        }
    };

    const getBase64 = (img, callback) => {
        const reader = new FileReader();
        reader.addEventListener('load', () => callback(reader.result));
        reader.readAsDataURL(img);
    }

    const beforeUpload = (file) => {
        const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png';
        if (!isJpgOrPng) {
            message.error('You can only upload JPG/PNG file!');
        }
        const isLt2M = file.size / 1024 / 1024 < 2;
        if (!isLt2M) {
            message.error('Image must smaller than 2MB!');
        }
        return isJpgOrPng && isLt2M;
    }

    let promoteComponent
    if (isMobileResolution) {
        promoteComponent = (
            <MobileComponent
                promote_image_url={promoteImageUrl}
                promote_form={promoteForm}
                errors={errors}
                check_before_upload={beforeUpload}
                handle_upload={handleUpload}
                set_form={(fieldName, value) => setform(fieldName, value)}
                add_promotion={addPromotion}
            />
        )
    } else {
        promoteComponent = (
            <WebComponent
                promote_image_url={promoteImageUrl}
                promote_form={promoteForm}
                errors={errors}
                check_before_upload={beforeUpload}
                handle_upload={handleUpload}
                set_form={(fieldName, value) => setform(fieldName, value)}
                add_promotion={addPromotion}
            />
        )
    }

    return promoteComponent
}